package ArrayProblems;import java.util.Scanner;/*For left rotate-arr[i] = arr[(i+D)%N]For right rotate-arr[i] = arr[(i+N-D)%N] */public class LeftAndRightRotation {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while (t > 0) {            int temp;            int n = sc.nextInt();            int d = sc.nextInt();            int[] a = new int[n];            for (int i = 0; i < n; i++) {                a[i] = sc.nextInt();            }            if(d > n){                d %=n;            }            for (int i = 0; i < n-d; i++) {                temp = a[i];                a[i] = a[i+d%n];                a[i+d%n] = temp;            }            for (int i = 0; i < n; i++) {                System.out.printf(a[i]+" ");            }            t--;        }    }}