package ArrayProblems;public class MergeSortedArray {    public static void main(String[] args) {        int[] nums1 =new int[]{};        int[] nums2 = new int[]{};        int m = nums1.length;        int n = nums2.length;        int k = 0,j;        for (int i = 0; i < m; i++) {            if(n == 0){                System.out.println(nums1);                break;            }            if(nums1[i] == 0){                if (k<n){                    nums1[i] = nums2[k++];                }            }            if(k<n && nums1[i]>= nums2[k]){                for ( j = m-1; j >i ; j--) {                    nums1[j]= nums1[j-1];                }                nums1[i]= nums2[k++];            }        }        for (int i = 0; i < m; i++) {            System.out.print(nums1[i]+" ");        }    }}