package Collection;import java.util.ArrayList;import java.util.Iterator;public class IteratorDemo {    public static void main(String[] args) {        ArrayList l = new ArrayList();        for (int i = 0; i <=10 ; i++) {            l.add(i);        }        System.out.println(l);        Iterator iterator = l.iterator();        while (iterator.hasNext()){            Integer integer = (Integer) iterator.next();            if(integer%2 == 0)                System.out.println(integer);            else                iterator.remove();        }        System.out.println(l);    }}