package Collection;import java.util.LinkedList;import java.util.ListIterator;public class ListIteratorDemo {    public static void main(String[] args) {        LinkedList l = new LinkedList();        l.add("A");        l.add("B");        l.add("C");        l.add("D");        l.add("E");        System.out.println(l);        ListIterator ltr = l.listIterator();        while (ltr.hasNext()){            String s = (String) ltr.next();            if(s.equals("B"))                ltr.remove();            else if (s.equals("C"))                ltr.add(123);            else if (s.equals("D"))                ltr.set("M");        }        System.out.println(l);    }}