package Collection;import java.util.Comparator;import java.util.TreeSet;// This example shows that we can have non comparable object in TreeSet if using Comparatorpublic class TreeSetDemo3 {    public static void main(String[] args) {//        TreeSet t = new TreeSet();  // we will get ClassCastException: java.lang.StringBuffer cannot be cast to java.lang.Comparable        TreeSet t = new TreeSet(new MyComparator2());        t.add(new StringBuffer("Z"));        t.add(new StringBuffer("A"));        t.add(new StringBuffer("B"));        System.out.println(t);    }}class MyComparator2 implements Comparator {    @Override    public int compare(Object o1, Object o2) {        String s1 = o1.toString();        String s2 = o2.toString();        return  s1.compareTo(s2);    }}