package Collection;import java.util.Comparator;import java.util.TreeSet;// This example shows that we can have non comparable and hetrogeneous object in TreeSet if using Comparatorpublic class TreeSetDemo4 {    public static void main(String[] args) {        TreeSet t = new TreeSet(new MyComparator4()); // Default Natural Sorting Order compareTo() method of Comparable class is called        t.add("A");        t.add(new StringBuffer("ABC"));        t.add(new StringBuffer("AA"));        t.add("XX");        t.add("ABCD");        t.add("A");        System.out.println(t);    }}class MyComparator4 implements Comparator {    @Override    public int compare(Object o1, Object o2) {        String s1 = o1.toString();        String s2 = o2.toString();        int length1 = s1.length();        int length2 = s2.length();        if (length1 < length2)            return -1;        else if (length1 > length2)            return 1;        else            return s1.compareTo(s2);    }}