package LinkedList;public class Node {    private int data;    private Node next;    public Node(int data) {        this.data = data;        this.next = null;    }    public Node getNext(){        return this.next;    }    public void setNext(Node next) {        this.next = next;    }    public int getData(){        return this.data;    }    public String toString(){        return String.valueOf(data);    }}class LinkedList {    public Node head = null;    public LinkedList(){    }    public int countNodes(){        if(head== null)            return 0;        else{            int count = 0;            Node curr = head;            while (curr !=null){                count++;                curr = curr.getNext();            }            return count;        }    }    public void addNode(int data){        if(head == null){            head = new Node(data);        }        else {            Node curr = head;            while (curr.getNext() !=null){                curr = curr.getNext();            }            curr.setNext(new Node(data));        }    }    public int popElement(){        if(head != null){            int topElement = head.getData();            head = head.getNext();            return topElement;        }        return -1;    }    public static void main(String[] args) {        LinkedList linkedList = new LinkedList();        linkedList.addNode(1);        linkedList.addNode(2);        linkedList.addNode(3);        linkedList.addNode(4);        System.out.println("+++++++++++++");        System.out.println(linkedList.popElement());System.out.println("+++++++++++++");        System.out.println(linkedList.popElement());System.out.println("+++++++++++++");        System.out.println(linkedList.popElement());System.out.println("+++++++++++++");        System.out.println(linkedList.popElement());System.out.println("+++++++++++++");        System.out.println(linkedList.popElement());    }}