public class PascalTriangleWithRecursion {    public static void main(String[] args) {        for (int i = 0; i < 7; i++) {            for (int k = 0; k <7-i ; k++) {                System.out.print(" ");            }            for (int j = 0; j <= i; j++) {                System.out.print(pascal(i,j) + " ");            }            System.out.println();        }    }    public static int pascal(int i, int j){        if(j == 0)            return 1;        if(i == j)            return 1;        else            return pascal(i-1,j-1)+ pascal(i-1,j);    }}