import java.util.HashMap;import java.util.Scanner;//fix34([3, 1, 4]) → [3, 4, 1]////        fix34([3, 4, 1]) → [3, 4, 1]////        fix34([1, 1, 1]) → [1, 1, 1]////        fix34([1]) → [1]////        fix34([]) → []////        fix34([7, 3, 7, 7, 4]) → [7, 3, 4, 7, 7]public class Test {    public static void main(String[] a) {        System.out.println(count8(123));        int[] nums = fix34(new int[]{1, 2, 3, 4, 4, 3, 7});        for (int i = 0; i < nums.length; i++) {            System.out.print(nums[i]+ " ");        }    }        public static int[] fix34(int[] nums) {        for (int i = 0; i < nums.length; i++) {            if (nums[i] == 3) {                if (nums[i + 1] != 4) {                    for (int k = 0; k < nums.length; k++) {                        if (nums[k] == 4 && nums[k - 1] != 3) {                            nums[i + 1] = nums[i + 1] + nums[k];                            nums[k] = nums[i + 1] - nums[k];                            nums[i + 1] = nums[i + 1] - nums[k];                        }                    }                }            }        }        return (nums);    }    public static int count8(int n) {        if (n == 8) {            return 1;        }        else if(n == n/10 &&  n!= 8){            return 0;        }        else if (n % 10 == 8) {            if (n % 100 == 88) {                return 2 + count8(n / 10);            }            return 1 + count8(n / 10);        }        else if (n % 10 != 8) {            return count8(n/10);        }        else {            return 0;        }    }}