import java.util.HashMap;import java.util.Map;public class TwoSum {    public static void main(String[] args) {        int[] a = new int[]{2,1,3,4};        int[] result = twoSum(a,3);        System.out.println(result[0]+" "+result[1]);    }    public static int[] twoSum(int[] nums, int target) {        Map<Integer, Integer> map = new HashMap<Integer,Integer>();        for (int i = 0; i < nums.length; i++) {            if (!map.containsKey(nums[i]))                map.put(nums[i], i);            int complement = target - nums[i];            if (map.containsKey(complement) && map.get(complement) != i) {                return new int[]{map.get(complement),i};            }        }        throw new IllegalArgumentException("No two sum solution");    }}